import React, { Component } from 'react';
import logo from './twitter-icon.png';
import './App.css';
import { Card, Icon, Input, Image} from 'semantic-ui-react'

class App extends Component {
  state = {
    tweets: []
  }

  onChange = event => 
    fetch(`http://localhost:8000/?text=${encodeURIComponent(event.target.value)}`)
      .then(res => res.json())
      .then(tweets => {
        console.log(tweets);
        return tweets.map(tweet => ({...tweet._source, score: tweet._score}))
      }).then(tweets => {
        this.setState({ tweets })
      });


  render() {
    return (
        <div className="App-header">
          <span className="title">
            <Image floated='left' size='mini' src={logo}/>
            <h1 style={{ display: 'inline' }}>Toogle</h1>
          </span>
          <div className="search-bar">
            <Input icon='search' iconPosition="left" type="text" onChange={this.onChange} />
          </div>
          <div className="center-tweets">
            <Card.Group itemsPerRow={2}>
              {/* {items} */}
              {this.state.tweets.map(tweet => (
                <Card href={tweet.url[0].url} target='_blank'>
                  <Card.Content>
                    <Card.Header style={{flex: 0}}>
                      <Image floated='left' size='mini' circular src={tweet.picture} />
                      {`@${tweet.userName}`}
                    </Card.Header>
                    <Card.Description>{tweet.text}</Card.Description>
                  </Card.Content>
                  <Card.Content extra>
                    <Icon name='user' />
                    {tweet.followers}
                  </Card.Content>
                </Card>  
              ))}

            </Card.Group>
          </div>
        </div>
    );
  }
}

export default App;
