import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import Test from './Test'
import { Card, Icon, Input, Image} from 'semantic-ui-react'
import newTweet from './newTweet'
import testTweet from './testTweet'

// var tweetArray = [newTweet, testTweet, testTweet, newTweet, newTweet, testTweet, testTweet, newTweet];

class App extends Component {
  state = {
    tweets: []
  }

  onChange = event => 
    fetch(`http://localhost:8000/?text=${encodeURIComponent(event.target.value)}`)
      .then(res => res.json())
      .then(tweets => {
        console.log(tweets);
        return tweets.map(tweet => ({...tweet._source, score: tweet.score}))
      }).then(tweets => {
        this.setState({ tweets })
      });


  render() {
    var items = [];
    var input = "";
    var url = "";

    // for (var i = 0; i < tweetArray.length; ++i) {
    //   url = "";
    //   if (tweetArray[i].entities.urls.length > 0) {
    //     url = tweetArray[i].entities.urls[0].url
    //   }
    //   items.push(<Test style={"card-header"} tweet={tweetArray[i]} url={url}/>);
    // }

    // const searchInput = () => <Input icon='search' iconPosition="left" placeholder='Search...' />

    return (
        <div className="App-header">
          <h1>Toogle</h1>
          <div className="search-bar">
            <Input icon='search' iconPosition="left" type="text" onChange={this.onChange} />
          </div>
          <div className="center-tweets">
            <Card.Group itemsPerRow={2}>
              {/* {items} */}
              {this.state.tweets.map(tweet => (
                <Card href={tweet.url.url} target='_blank'>
                  <Card.Content>
                    <Card.Header style={{flex: 0}}>
                      <Image floated='left' size='mini' circular src={tweet.picture} />
                      {`@${tweet.userName}`}
                    </Card.Header>
                    <Card.Description>{tweet.text}</Card.Description>
                  </Card.Content>
                  <Card.Content extra>
                    <Icon name='user' />
                    {tweet.followers}
                  </Card.Content>
                </Card>  
              ))}

            </Card.Group>
          </div>
        </div>
    );
  }
}

export default App;
